FROM vacancies WHERE public_opening_date IS NOT NULL AND public_opening_date <= current_timestamp AND ((point(:searchFromLongitudeValue, :searchFromLatitudeValue) <@> point(longitude, latitude) < :distance) OR (regions ILIKE :region) OR (overseasjob = true)) AND closing_date > current_timestamp AND CONCAT(title, ' ', description) ILIKE :keyword AND dept_id in (:dept0,:dept1) AND coalesce(salary_max, salary_min) >= :salary_min AND salary_min <= :salary_max