SELECT count(*) FROM vacancies WHERE public_opening_date IS NOT NULL AND public_opening_date <= current_timestamp AND ((point(:searchFromLongitudeValue, :searchFromLatitudeValue) <@> point(longitude, latitude) < :distance) AND (overseasjob = :overseasJob OR overseasjob IS NULL) AND closing_date > current_timestamp AND CONCAT(title, ' ', description) ILIKE :keyword